// Desktop.tsx
import React, { useState } from "react";
import Workout from "./Components/Cards";
import NavBar from "./Components/NavBar";
import WorkoutPlan from "./Components/WorkoutPlans";
import "./Desktop.css";
import { Button, Container } from "@mui/material";
import { Link } from 'react-router-dom';

interface WorkoutData {
  id: number;
  title: string;
  description: string;
  image: string;
  onClickPlan: () => void;
  workoutPlan: {
    workouts: { name: string; reps: number; sets: number }[];
  };
}

const Desktop: React.FC = () => {
  const [showWorkoutPlan, setShowWorkoutPlan] = useState(false);
  const [selectedWorkoutIndex, setSelectedWorkoutIndex] = useState<number | null>(null);

  const workoutData: WorkoutData[] = [
    {
      id: 1,
      title: 'Workout 1',
      description: 'This is workout 1 consisting of Workouts',
      image: './images/Workout1.jpg',
      onClickPlan: () => handleWorkoutClick(0),
      workoutPlan: {
        workouts: [
          { name: 'Exercise A', reps: 10, sets: 3 },
          { name: 'Exercise B', reps: 12, sets: 4 },
          { name: 'Exercise C', reps: 8, sets: 4 },
          // Add more exercise data as needed
        ],
      },
    },
    {
      id: 2,
      title: 'Workout 2',
      description: 'This is workout 2 consisting of Workouts',
      image: './images/Workout2.jpg',
      onClickPlan: () => handleWorkoutClick(1),
      workoutPlan: {
        workouts: [
          { name: 'Exercise X', reps: 15, sets: 3 },
          { name: 'Exercise Y', reps: 10, sets: 4 },
          { name: 'Exercise Z', reps: 12, sets: 4 },
          // Add more exercise data as needed
        ],
      },
    },
    // Add more workout data as needed
  ];

  const handleWorkoutClick = (index: number) => {
    setSelectedWorkoutIndex(index);
    setShowWorkoutPlan(true);
  };

  const onNavigateBack = () => {
    setShowWorkoutPlan(false);
    setSelectedWorkoutIndex(null);
  };

  return (
    <Container>
      <NavBar onNavigateBack={onNavigateBack} />
      <div style={{ display: 'flex', justifyContent: 'center', padding: '20px', flexWrap: 'wrap' }}>
        {workoutData.map((workout) => (
          <Workout key={workout.id} {...workout} />
        ))}
      </div>
      {showWorkoutPlan && selectedWorkoutIndex !== null && (
        <WorkoutPlan workout={workoutData[selectedWorkoutIndex]} />
      )}
    </Container>
  );
};

export default Desktop;
