// WorkoutPlan.tsx
import React, { useState } from 'react';
import { Container, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import WorkoutDetails from './WorkoutDetails';
import WorkoutSummary from './WorkoutSummary'; // Import the WorkoutSummary component

interface Workout {
  name: string;
  reps: number;
  sets: number;
}

interface WorkoutPlanProps {
  workoutImage: string;
  workouts: Workout[];
  startButtonColor: string;
}

const WorkoutPlan: React.FC<WorkoutPlanProps> = ({ workoutImage, workouts, startButtonColor }) => {
  const navigate = useNavigate();
  const [selectedWorkoutIndex, setSelectedWorkoutIndex] = useState<number | null>(null);

  const handleStartWorkout = (index: number) => {
    setSelectedWorkoutIndex(index);
    navigate(`/workout-details/${index}`);
  };
  const handleNextWorkout = () => {
    if (selectedWorkoutIndex !== null && selectedWorkoutIndex + 1 < workouts.length) {
      setSelectedWorkoutIndex(selectedWorkoutIndex + 1);
    } else {
      // Navigate to WorkoutSummary when the last workout is completed
      navigate('/workout-summary');
    }
  };

  const handleEndWorkout = () => {
    // Handle any cleanup or actions needed when ending the workout
    setSelectedWorkoutIndex(null); // Reset the selected workout index
  };

  return (
    <Container maxWidth="md" sx={{ textAlign: 'center', mt: 3 }}>
      {selectedWorkoutIndex !== null ? (
        <WorkoutDetails
          workoutName={workouts[selectedWorkoutIndex].name}
          nextWorkoutHandler={selectedWorkoutIndex === workouts.length - 1 ? handleEndWorkout : handleNextWorkout}
        />
      ) : (
        <>
          {/* ... */}
        </>
      )}
    </Container>
  );
};

export default WorkoutPlan;
