// WorkoutPlan.tsx
import React, { useState } from 'react';
import { Container, Typography, Button } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import WorkoutDetails from './WorkoutDetails';

interface Workout {
  name: string;
  reps: number;
  sets: number;
}

interface WorkoutPlanProps {
  workoutImage: string;
  workouts: Workout[];
  startButtonColor: string;
}

const WorkoutPlan: React.FC<WorkoutPlanProps> = () => {
    const { id } = useParams(); // Extract the 'id' from route parameters
  
    // Assuming you have the workout data available
    const workouts = [
      { name: 'Workout 1', reps: 10, sets: 3 },
      { name: 'Workout 2', reps: 12, sets: 4 },
      { name: 'Workout 3', reps: 8, sets: 4 },
      // Add more workout data as needed
    ];
  
    const workout = workouts.find((w) => w.name === `Workout ${id}`);
  
    if (!workout) {
      // Handle case where workout is not found
      return (
        <Container maxWidth="md" sx={{ textAlign: 'center', mt: 3 }}>
          <Typography variant="h4" color="primary" gutterBottom>
            Workout Not Found
          </Typography>
        </Container>
      );
    }
  
    return (
      <Container maxWidth="md" sx={{ textAlign: 'center', mt: 3 }}>
        <Typography variant="h4" color="primary" gutterBottom>
          Workout Plan for {workout.name}
        </Typography>
        <Typography variant="h5" color="white" paragraph>
          Reps: {workout.reps} | Sets: {workout.sets}
        </Typography>
        {/* Display additional workout details as needed */}
      </Container>
    );
  };
  
  export default WorkoutPlan;
