// WorkoutPlan.tsx
import React, { useState } from 'react';
import { Container, Typography, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import WorkoutDetails from './WorkoutDetails';

interface Workout {
  name: string;
  reps: number;
  sets: number;
}

interface WorkoutPlanProps {
  workout: {
    id: number;
    title: string;
    description: string;
    image: string;
    onClickPlan: () => void;
    workoutPlan: {
      workouts: Workout[];
    };
  };
}

const WorkoutPlan: React.FC<WorkoutPlanProps> = ({ workout }) => {
  const navigate = useNavigate();
  const [selectedWorkoutIndex, setSelectedWorkoutIndex] = useState<number | null>(null);

  const handleStartWorkout = () => {
    setSelectedWorkoutIndex(workout.id);
    navigate('/workout-details');
  };

  const handleNextWorkout = () => {
    if (selectedWorkoutIndex !== null) {
      setSelectedWorkoutIndex(null);
    }
  };

  const handlePreviousWorkout = () => {
    if (selectedWorkoutIndex !== null) {
      setSelectedWorkoutIndex(null);
    }
  };

  const handleEndWorkout = () => {
    // Handle any cleanup or actions needed when ending the workout
    setSelectedWorkoutIndex(null); // Reset the selected workout index
  };

  return (
    <Container maxWidth="md" sx={{ textAlign: 'center', mt: 3 }}>
      {selectedWorkoutIndex !== null ? (
        <WorkoutDetails
          workoutName={workout.title}
          nextWorkoutHandler={selectedWorkoutIndex === workout.workoutPlan.workouts.length - 1 ? handleEndWorkout : handleNextWorkout}
        />
      ) : (
        <>
          <img src={workout.image} alt={`Workout ${workout.id} Preview`} style={{ width: '100%', backgroundColor: 'white' }} />
          {workout.workoutPlan.workouts.map((exercise, index) => (
            <div key={exercise.name} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
              <Typography variant="body1" color="white">
                {exercise.name}
              </Typography>
              <Typography variant="body1" color="white">
                {`${exercise.reps} reps / ${exercise.sets} sets`}
              </Typography>
            </div>
          ))}
          <Button variant="contained" size="large" color="primary" onClick={handleStartWorkout}>
            Start Workout
          </Button>
        </>
      )}
      {selectedWorkoutIndex !== null && (
        <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: '8px' }}>
          {selectedWorkoutIndex > 0 && (
            <Button variant="contained" size="large" onClick={handlePreviousWorkout}>
              Previous Workout
            </Button>
          )}
          {selectedWorkoutIndex < workout.workoutPlan.workouts.length - 1 && (
            <Button variant="contained" size="large" onClick={handleNextWorkout}>
              Next Workout
            </Button>
          )}
          {selectedWorkoutIndex === workout.workoutPlan.workouts.length - 1 && (
            <Button variant="contained" size="large" onClick={handleEndWorkout}>
              End Workout
            </Button>
          )}
        </Box>
      )}
    </Container>
  );
};

export default WorkoutPlan;
