// WorkoutPlan.tsx
import React, { useState } from 'react';
import { Container, Typography, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import WorkoutDetails from './WorkoutDetails';

interface Workout {
  name: string;
  reps: number;
  sets: number;
}

interface WorkoutPlanProps {
  workoutImage: string;
  workouts: Workout[];
}

const WorkoutPlan: React.FC<WorkoutPlanProps> = ({ workoutImage, workouts, startButtonColor }) => {
  const navigate = useNavigate();
  const [selectedWorkoutIndex, setSelectedWorkoutIndex] = useState<number | null>(null);

  const handleStartWorkout = (index: number) => {
    setSelectedWorkoutIndex(index);
    navigate('/workout-details');
  };

  const handleNextWorkout = () => {
    if (selectedWorkoutIndex !== null && selectedWorkoutIndex + 1 < workouts.length) {
      setSelectedWorkoutIndex(selectedWorkoutIndex + 1);
    }
  };

  const handlePreviousWorkout = () => {
    if (selectedWorkoutIndex !== null && selectedWorkoutIndex > 0) {
      setSelectedWorkoutIndex(selectedWorkoutIndex - 1);
    }
  };

  const handleEndWorkout = () => {
    // Handle any cleanup or actions needed when ending the workout
    setSelectedWorkoutIndex(null); // Reset the selected workout index
  };

  return (
    <Container maxWidth="md" sx={{ textAlign: 'center', mt: 3 }}>
      {selectedWorkoutIndex !== null ? (
        <WorkoutDetails
          workoutName={workouts[selectedWorkoutIndex].name}
          nextWorkoutHandler={selectedWorkoutIndex === workouts.length - 1 ? handleEndWorkout : handleNextWorkout}
        />
      ) : (
        <>
          <img src={workoutImage} alt="Workout Preview" style={{ width: '100%', backgroundColor: 'white' }} />
          {workouts.map((workout, index) => (
            <div key={workout.name} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
              <Typography variant="body1" color="white">
                {workout.name}
              </Typography>
              <Typography variant="body1" color="white">
                {`${workout.reps} reps / ${workout.sets} sets`}
              </Typography>
              <Button
                variant="contained"
                size="large"
                style={{ backgroundColor: startButtonColor }}
                onClick={() => handleStartWorkout(index)}
              >
                Start Workout
              </Button>
            </div>
          ))}
        </>
      )}
      {selectedWorkoutIndex !== null && (
        <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: '8px' }}>
          {selectedWorkoutIndex > 0 && (
            <Button variant="contained" size="large" onClick={handlePreviousWorkout}>
              Previous Workout
            </Button>
          )}
          {selectedWorkoutIndex < workouts.length - 1 && (
            <Button variant="contained" size="large" onClick={handleNextWorkout}>
              Next Workout
            </Button>
          )}
          {selectedWorkoutIndex === workouts.length - 1 && (
            <Button variant="contained" size="large" onClick={handleEndWorkout}>
              End Workout
            </Button>
          )}
        </Box>
      )}
    </Container>
  );
};

export default WorkoutPlan;
