// Desktop.tsx
import React, { useState } from "react";
import Workout from "./Components/Cards";
import NavBar from "./Components/NavBar";
import WorkoutPlan from "./Components/WorkoutPlans";
import "./Desktop.css";

export interface Exercise {
  name: string;
  reps: number;
  sets: number;
}

export interface Workout {
  id: number;
  title: string;
  description: string;
  image: string;
  onClickPlan: () => void;
  exercises: Exercise[];
}

const convertWorkoutDataToWorkout = (workoutData: WorkoutData): Workout => {
  return {
    id: workoutData.id,
    title: workoutData.title,
    description: workoutData.description,
    image: workoutData.image,
    onClickPlan: workoutData.onClickPlan,
    exercises: workoutData.exercises,
  };
};

export interface WorkoutData {
  id: number;
  title: string;
  description: string;
  image: string;
  onClickPlan: () => void;
  exercises: Exercise[];
}

const Desktop: React.FC = () => {
  const [showWorkoutPlan, setShowWorkoutPlan] = useState(false);

  const workoutData: WorkoutData[] = [
    // Your workout data here
  ];

  const workouts: Workout[] = workoutData.map(convertWorkoutDataToWorkout);

  const navigateBackToDesktop = () => {
    setShowWorkoutPlan(false);
  };

  return (
    <div>
      <NavBar onNavigateBack={navigateBackToDesktop} />
      {showWorkoutPlan ? (
        <WorkoutPlan workouts={workouts} />
      ) : (
        <div style={{ display: 'flex', justifyContent: 'center', padding: '20px', flexWrap: 'wrap' }}>
          {workouts.map((workout) => (
            <Workout key={workout.id} {...workout} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Desktop;
